import { HttpClient } from '@angular/common/http';
import { DeviceDto } from '../shared/models/device-dto/deviceDto.model';
import { Injectable } from '@angular/core';
import { tap } from 'rxjs/operators';
import { Observable } from 'rxjs';
@Injectable({
  providedIn: 'root'
})
export class DeviceService {


  private _deviceDtoList: DeviceDto[] = [];

  constructor(private httpClient: HttpClient) { }

  get deviceDtoList(): DeviceDto[]{
    return this._deviceDtoList;
  }

  set deviceDtoList(deviceDtoList: DeviceDto[]){
    this._deviceDtoList = deviceDtoList
  }

//todo routing to page not found if device absent

async fillDeviceList(){
  const t = await this.httpClient.get<DeviceDto[]>('http://127.0.0.1:9069/device/getall').toPromise();
}

  getDeviceDetails(macAdr: String): DeviceDto | null{
    if(!this.deviceDtoList?.length)
    {
      console.log('fetching data');


     this.httpClient.get<DeviceDto[]>(
        'http://127.0.0.1:9069/device/getall').subscribe(
          response =>{
            this._deviceDtoList = response;
            console.log(this._deviceDtoList);
          }
        )
      console.log(this._deviceDtoList);
    }
    let foundDevice = this._deviceDtoList.find(device => device.deviceDescription.macAdr === macAdr);
    return foundDevice || null;
  }

  fetchDeviceList(): Observable<DeviceDto[]>{
   return this.httpClient.get<DeviceDto[]>(
     'http://127.0.0.1:9069/device/getall').pipe(
      tap(response =>{
        this._deviceDtoList = response;
      })
     )

  }
}
